# Protein Digest# Write a simple web-server application using TurboGears to carry out #   an in silico enzymatic digest of a user-provided protein sequence.from urllib.error import HTTPError# user should specify:    # min and max length    # min and max molec weight    # Number of missed cleavages    # specific enzyme    # Outout a table of peptides with:    # peptide length    # molecular weight    # Number of missed cleavages    # amino acids left and right of each peptide in the proteins seqimport custom_io as ioimport sysfrom digest import *#### Importing data into programtry:    # Sequence    seq_input = sys.argv[1]    # Length    min_length = sys.argv[2]    max_length = sys.argv[3]    # Molecular weight    min_weight = sys.argv[4]    max_weight = sys.argv[5]    #number of missed cleavages    missed_cleavages = sys.argv[6]    # Specific enzyme    enzyme = sys.argv[7]except IndexError:    print("Insufficient arguments given")        # testing    seq_input = "P12123"    min_l = 0    max_l = 999    min_w = 0    max_w = 10000000    missed_cleavages = 2            # sys.exit(1)#### Logical error checkingif min_l > max_l:    print("Minimum peptide length can't be larger than maximum peptide length",          file = sys.stderr)    sys.exit(1)    if min_w > max_w:    print("Minimum molecular length can't be larger than maximum molecular length",          file = sys.stderr)    sys.exit(1)    #### Programif seq_input.find(".") != -1: # If a "." is in a file name:    try:        seq = io.read_file(seq_input)    except FileNotFoundError:        print("File not found on OS")else:    seq = io.access_uniprot(seq_input)seq = Seq(UniProt_acc = seq_input)all_frags = digest(seq, Trypsin(), min_l,max_l,min_w,max_w,missed_cleavages)io.make_frag_table(all_frags)